class DBHelper{static get DATASERVICES_URL(){return"http://localhost:1337"}static get DATASERVICE_RESTAURANTS_URL(){return`${DBHelper.DATASERVICES_URL}/restaurants`}static fetchRestaurants(e){fetch(DBHelper.DATASERVICE_RESTAURANTS_URL).then(e=>e.json()).then(t=>{e&&e(null,t)}).catch(t=>{e?e(t):console.log(t)})}static fetchRestaurantById(e,t){DBHelper.fetchRestaurants((l,n)=>{if(l)t(l,null);else{const l=n.find(t=>t.id==e);l?t(null,l):t("Restaurant does not exist",null)}})}static fetchRestaurantByCuisine(e,t){DBHelper.fetchRestaurants((l,n)=>{if(l)t(l,null);else{const l=n.filter(t=>t.cuisine_type==e);t(null,l)}})}static fetchRestaurantByNeighborhood(e,t){DBHelper.fetchRestaurants((l,n)=>{if(l)t(l,null);else{const l=n.filter(t=>t.neighborhood==e);t(null,l)}})}static fetchRestaurantByCuisineAndNeighborhood(e,t,l){DBHelper.fetchRestaurants((n,r)=>{if(n)l(n,null);else{let n=r;"all"!=e&&(n=n.filter(t=>t.cuisine_type==e)),"all"!=t&&(n=n.filter(e=>e.neighborhood==t)),l(null,n)}})}static fetchNeighborhoods(e){DBHelper.fetchRestaurants((t,l)=>{if(t)e(t,null);else{const t=l.map((e,t)=>l[t].neighborhood),n=t.filter((e,l)=>t.indexOf(e)==l);e(null,n)}})}static fetchCuisines(e){DBHelper.fetchRestaurants((t,l)=>{if(t)e(t,null);else{const t=l.map((e,t)=>l[t].cuisine_type),n=t.filter((e,l)=>t.indexOf(e)==l);e(null,n)}})}static urlForRestaurant(e){return`${UrlHelper.ROOT_URL}restaurant.html?id=${e.id}`}static imageUrlForRestaurant(e,t=null){let l=e.photograph;return l?(l.endsWith(".jpg")||(l+=".jpg"),t&&(l=l.replace(/.jpg$/,`${t}.jpg`)),`${UrlHelper.ROOT_URL}img/${l}`):null}static mapMarkerForRestaurant(e,t){return new google.maps.Marker({position:e.latlng,title:e.name,url:DBHelper.urlForRestaurant(e),map:t,animation:google.maps.Animation.DROP})}}